mapper.select:
SELECT sum( pbr.qty * pbr.capacity ) AS capacity, pbr.proj_id, pbr.model, pb.proj_name as name, dt.`text` FROM project_bom_relation pbr left join proj_base pb on pbr.proj_id = pb.id left join ( SELECT dict.dict_code, item.item_text AS "text", item.item_value AS "value" FROM sys_dict_item item INNER JOIN sys_dict dict ON dict.id = item.dict_id where dict_code = 'model' ) dt on pbr.model = dt.value where pbr.del_flag = '0' and pbr.model is not null
and pbr.proj_id = #{query.projectId}
and pbr.model = #{query.model}
group by pbr.proj_id,pbr.model

SELECT sum((pbr.capacity - co.capacity) * co.qty) as capacity, pbr.model, cb.project_id as proj_id, sum(ifnull(vcra.contract_amount_tax_local,co.contract_amount_tax_local)) as budget_amount FROM contract_object co INNER JOIN contract_base cb ON co.contract_id = cb.id INNER JOIN bas_material bm on co.prod_code = bm.code INNER JOIN project_bom_relation pbr ON bm.id = pbr.material_id AND pbr.del_flag = '0' AND pbr.proj_id = cb.project_id left join view_contract_record_amount vcra on co.id = vcra.record_id WHERE co.del_flag = '0' AND cb.contract_status = '4' AND cb.main_id is null
AND pbr.model = #{query.model}
AND cb.project_id = #{query.projectId}
group by cb.project_id,pbr.model

SELECT pbc.id, pbc.model, prb.budget_amount, project_id, iprogress, neck, capacity FROM project_bom_child pbc LEFT JOIN ( SELECT sum( qty * budget_price ) AS budget_amount, model FROM project_bom_relation WHERE del_flag = '0'
AND proj_id = #{query.projectId}
GROUP BY model ) prb ON pbc.model = prb.model WHERE pbc.del_flag = '0'
AND pbc.project_id = #{query.projectId}
