mapper.select:
SELECT pbm.qty, pbm.budget_price, pbm.budget_amount, pbm.material_id, pbm.material_id as id, pbm.capacity, bm.`name`, bm.code, bm.spe_type, bm.model, bm.country, ifnull(tp.qty,0) as has_req_qty, ifnull(co.qty,0) as has_buy_qty, ifnull(sibe.qty,0) as stock_qty, ifnull(co.qty,0) - ifnull(sibe.qty,0) as on_road_qty, ifnull(tp.qty,0) - ifnull(co.qty,0) as remain_qty FROM project_bom_relation pbm left join bas_material bm on pbm.material_id = bm.id left join ( SELECT prm.project_id, prd.prod_id, sum( prd.qty ) AS qty FROM purchase_request_detail prd LEFT JOIN purchase_request_main prm ON prd.req_id = prm.id WHERE prm.req_type = '0' and prd.del_flag = '0' GROUP BY prm.project_id, prd.prod_id ) tp on pbm.material_id = tp.prod_id and pbm.proj_id = tp.project_id left join ( SELECT cb.project_id, co.prod_code, sum(co.qty) as qty FROM contract_object co LEFT JOIN contract_base cb ON co.contract_id = cb.id WHERE co.del_flag = '0' AND cb.contract_status = '4' GROUP BY cb.project_id, co.prod_code ) co on bm.code = co.prod_code and pbm.proj_id = co.project_id left join ( SELECT sibe.project_id, sibe.material_id, sum(sibe.qty) as qty FROM stk_io_bill_entry sibe WHERE sibe.del_flag = '0' GROUP BY sibe.project_id, sibe.material_id )sibe on pbm.material_id = sibe.material_id and pbm.proj_id = sibe.project_id where pbm.del_flag = '0' and pbm.proj_id = #{query.projId} and pbm.category_id = #{query.categoryId}
AND bm.code LIKE concat(concat('%',#{query.code}),'%')
AND bm.name LIKE concat(concat('%',#{query.name}),'%')
AND bm.model = #{query.model}

SELECT pbm.qty, pbm.qty - ifnull(tp.qty,0) as max_qty, ifnull(tp.qty,0) as has_req_qty, pbm.budget_price, pbm.budget_amount, pbm.material_id, pbm.material_id as id, pbm.capacity, bm.`name`, bm.code, bm.spe_type, bm.model, bm.unit_id FROM project_bom_relation pbm left join bas_material bm on pbm.material_id = bm.id left join ( SELECT prm.project_id, prd.prod_id, sum( ifnull(bi.qty,prd.qty) ) AS qty, prm.category_id FROM purchase_request_detail prd LEFT JOIN purchase_request_main prm ON prd.req_id = prm.id LEFT JOIN ( select ir.to_record_id,sum(iss.inquiry_qty) as qty from inquiry_record ir inner join inquiry_supplier iss on ir.id = iss.record_id where ir.del_flag = '0' and iss.is_recommend = '1' and iss.status = '4' group by ir.to_record_id union all select br.to_record_id,sum(brs.bidding_qty) as qty from bidding_record br inner join bidding_record_supplier brs on br.id = brs.record_id where br.del_flag = '0' and brs.is_recommend = '1' group by br.to_record_id ) bi on prd.id = bi.to_record_id WHERE prm.req_type = '0' and prm.req_status != '4' AND prd.del_flag = '0'
AND prm.id != #{query.reqId}
GROUP BY prm.project_id, prd.prod_id, prm.category_id ) tp on pbm.material_id = tp.prod_id and pbm.proj_id = tp.project_id AND pbm.category_id = tp.category_id where pbm.del_flag = '0' and pbm.proj_id = #{query.projId} and pbm.category_id = #{query.categoryId} and pbm.qty - ifnull(tp.qty,0) > 0
AND bm.code LIKE concat(concat('%',#{query.code}),'%')
AND bm.name LIKE concat(concat('%',#{query.name}),'%')
AND bm.model = #{query.model}

SELECT sum(pbm.qty) as qty, sum(ifnull(tp.qty,0)) as hasReqQty, sum(ifnull(co.qty,0)) as hasBuyQty, sum(ifnull(sibe.qty,0)) as stockQty, sum(ifnull(co.qty,0) - ifnull(sibe.qty,0)) as onRoadQty, sum(ifnull(tp.qty,0) - ifnull(co.qty,0)) as remainQty FROM project_bom_relation pbm left join bas_material bm on pbm.material_id = bm.id left join ( SELECT prm.project_id, prd.prod_id, sum( prd.qty ) AS qty FROM purchase_request_detail prd LEFT JOIN purchase_request_main prm ON prd.req_id = prm.id WHERE prm.req_type = '0' and prd.del_flag = '0' GROUP BY prm.project_id, prd.prod_id ) tp on pbm.material_id = tp.prod_id and pbm.proj_id = tp.project_id left join ( SELECT cb.project_id, co.prod_code, sum(co.qty) as qty FROM contract_object co LEFT JOIN contract_base cb ON co.contract_id = cb.id WHERE co.del_flag = '0' AND cb.contract_status = '4' GROUP BY cb.project_id, co.prod_code ) co on bm.code = co.prod_code and pbm.proj_id = co.project_id left join ( SELECT sibe.project_id, sibe.material_id, sum(sibe.qty) as qty FROM stk_io_bill_entry sibe WHERE sibe.del_flag = '0' GROUP BY sibe.project_id, sibe.material_id )sibe on pbm.material_id = sibe.material_id and pbm.proj_id = sibe.project_id where pbm.del_flag = '0' and pbm.proj_id = #{query.projId} and pbm.category_id = #{query.categoryId}
AND bm.code LIKE concat(concat('%',#{query.code}),'%')
AND bm.name LIKE concat(concat('%',#{query.name}),'%')
AND bm.model = #{query.model}
